from flask import Flask
app = Flask(__name__)



@app.route('/')
def hello_world():
    return '<h1 style="text-align:center"text>Hello, World!</h1>'\
            '<p>This is a paragraph</p>'\
            '<img src="data:image/jpeg;base64,">'





@app.route("/username/<name>")
def greet(name):
    return f"Hello {name}"



if __name__== "__main__":
    app.run(debug=True)